svg.script:
function show(evt, on)
{
  /* This */
  var e = parent.document.embeds[1];
  var div = e.parentElement;
  var val;

  if(on)
     val = "obama.svg";
  else
     val = "circle.svg";


   var newE = div.appendChild(parent.document.createElement('embed'))
   newE.src = val;
   newE.width = e.width;
   newE.height = e.height;
   newE.type = e.type;

   div.removeChild(e);
}
